package com.algorithm.decimal;

import org.junit.Test;

import static org.junit.Assert.assertEquals;

public class NumberOfIntegersInAStringTest {

    @Test
    public void numDifferentIntegers() {
        NumberOfIntegersInAString.SetSolution setSolution = new NumberOfIntegersInAString.SetSolution();
        assertEquals(3, setSolution.numDifferentIntegers("a123bc34d8ef34"));
        assertEquals(2, setSolution.numDifferentIntegers("leet1234code234"));
        assertEquals(1, setSolution.numDifferentIntegers("a1b01c001"));
        assertEquals(0, setSolution.numDifferentIntegers("u"));
        assertEquals(1, setSolution.numDifferentIntegers("167278959591294"));
        assertEquals(3, setSolution.numDifferentIntegers(""));
        assertEquals(1, setSolution.numDifferentIntegers("0a0"));
    }
}