package com.algorithm.string;

import org.junit.Test;

import static org.junit.Assert.assertFalse;
import static org.junit.Assert.assertTrue;

public class PalindromePermutationLcciTest {

    @Test
    public void canPermutePalindrome() {
        PalindromePermutationLcci service = new PalindromePermutationLcci();
        assertTrue(service.canPermutePalindrome("tactcoa"));
        assertTrue(service.canPermutePalindrome("aab"));
        assertFalse(service.canPermutePalindrome(""));

    }
}